package persistence.connection;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class GenericDao implements iGenericDao {	private Connection c;	public Connection getConnection() throws ClassNotFoundException, SQLException {		String hostName = "localhost";		String user ="caio_note";		String senha ="123456";		String database = "requisicao";		Class.forName("net.sourceforge.jtds.jdbc.Driver");		String conexao = String.format("jdbc:jtds:sqlserver://%s:1433/%s;user=%s;password=%s;", hostName, database, user, senha);		c = DriverManager.getConnection(conexao);		return c;			}}